t().top - navbar.outerHeight(); // calculate the new scroll position ('body,html').animate({ // animate the scroll scrollTop: scrollPosition + 'px' }); }); @charset \\"utf-8\\"; /* CSS Document */ * { margin: 0; padding: 0; box-sizing: border-box; } #Produktet { padding-bottom: 200px; } html, body { background-color: #fff; color: #000000; font-family: 'Lato', 'Arial', sans-serif; font-weight: 300; font-size: 1.1rem; text-rendering: optimizeLegibility; overflow-x: hidden; } h1, h2, h3 { font-weight: 300; text-transform: uppercase; } h1 { margin-top: 0; margin-bottom: 2%; color: #fff; font-size: 2.4em; word-spacing: 0.8%; letter-spacing: 0.4%; font-weight: bold; } h2 { font-size: 1.8em; word-spacing: 0.4%; text-align: center; margin-bottom: 3, 5%; letter-spacing: 0.4%; } h3 { font-size: 1.1em; margin-bottom: 4%; } h2:after { display: block; height: 0.4%; background-color: #FFD700; content: \\" \\"; width: 10%; margin: 0 auto; margin-top: 3%; } header { background-image: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url(\\"img/citera hjemmeside baggrund (mindre).png\\"); background-size: cover; background-position: center; height: 100vh; background-attachment: fixed; } .logo { height: 110px; width: auto; float: left; margin-top: 1%; margin-left: 3%; } .logo2 { width: 200px; height: auto; padding-top: 2%; } .logo3 { width: 200px; height: auto; padding-top: 2%; } .hero-text-box h3 { color: white; margin-bottom: 5%; } .hero-text-box { position: absolute; width: 1140px; top: 50%; left: 50%; -webkit-transform: translate(-50%, -50%); -ms-transform: translate(-50%, -50%); transform: translate(-50%, -50%); } .hero-text-box h1, h3 { position: relative; animation: heading; animation-duration: 3s; animation-fill-mode: forwards; } @keyframes heading { 0% { top: -500px } 100% { top: 15px } } .top-box h2 { font-weight: bold; } .showcase { display: grid; grid-template-columns: repeat(auto-fit, minmax(20%, 1fr)); } .showcase img { width: 300%; height: 100%; } .showcase-left