VER_LOCATION = 5; // 语音 private final int TYPE_SEND_VOICE = 6; private final int TYPE_RECEIVER_VOICE = 7; //群成员变动 private final int TYPE_GROUP_CHANGE = 8; //自定义消息 private final int TYPE_CUSTOM_TXT = 9; private final MediaPlayer mp = new MediaPlayer(); private AnimationDrawable mVoiceAnimation; private FileInputStream mFIS; private FileDescriptor mFD; private Activity mActivity; private boolean autoPlay = false; private int nextPlayPosition = 0; private boolean mIsEarPhoneOn; //当前第0项消息的位置 private int mStart; //上一页的消息数 private int mOffset = PAGE_MESSAGE_COUNT; private boolean mHasLastPage = false; private Dialog mDialog; //发送图片消息的队列 private Queue<Message> mMsgQueue = new LinkedList<Message>(); private int mSendMsgId; private float mDensity; private int mWidth; private Animation mSendingAnim; private ContentLongClickListener mLongClickListener; private String mTargetAppKey; public MsgListAdapter(Context context, String targetId, String appKey, ContentLongClickListener longClickListener) { initData(context); this.mTargetId = targetId; if (appKey!= null) { mTargetAppKey = appKey; this.mConv = JMessageClient.getSingleConversation(mTargetId, appKey); } else { this.mConv = JMessageClient.getSingleConversation(mTargetId); } this.mLongClickListener = longClickListener; this.mMsgList = mConv.getMessagesFromNewest(0, mOffset); reverse(mMsgList); mStart = mOffset; UserInfo userInfo = (UserInfo) mConv.getTargetInfo(); if (!TextUtils.isEmpty(userInfo.getAvatar())) { userInfo.getAvatarBitmap(new GetAvatarBitmapCallback() { @Override public void gotResult(int status, String desc, Bitmap bitmap) { if (status == 0) { notifyDataSetChanged(); } else { HandleResponseCode.onHandle(mContext, status, false); } } }); } checkSendingImgMsg(); } private void reverse(List<Message> list) { if (list.size() >0 ){ Collections.reverse(list); } } public MsgListAdapter(Context context, long groupId, ContentLongClickListener longClickListener) { initData(context); this.mGroupId = groupId; this.mIsGroup = true; this.mLongClickListener = longClickListener; this.mConv = JMessageClient.getGroupConversation(groupId); this.mMsgList = mConv.getMessagesFromNewest(0, mOffset); reverse(mMsgList); mStart = mOffset; checkSendingImgMsg(); } private void initData(Context context) { this.mContext = context; mActivity = (Activity) context; DisplayMetrics dm = new DisplayMetrics(); mActivity.getWindowManager().getDefaultDisplay().getMetrics(dm); mWidth = dm.widthPixels; mDensity = dm.density; mInflater = LayoutInflater.from(mContext); mSendingAnim = AnimationUtils.loadAnimation(mContext, IdHelper.getAnim(mContext, \\"jmui_rotate\\")); LinearInterpolator lin = new LinearInterpolator(); mSendingAnim.setInterpolator(lin); AudioManager audioManager = (AudioManager) mContext .getSystemService(Context.AUDIO_SERVICE); audioManager.setMode(AudioManager.MODE_NORMAL); if (audioManager.isSpeakerphoneOn()) { audioManager.setSpeakerphoneOn(true); } else { audioManager.setSpeakerphoneOn(false); } mp.setAudioStreamType(AudioManager.STREAM_RING); mp.setOnErrorListener(new OnErrorListener() { @Override public boolean onError(MediaPlayer mp, int what, int extra) { return false; } }); } public void dropDownToRefresh() { if (mConv!= null) { List<Message> msgList = mConv.getMessagesFromNewest(mStart, PAGE_MESSAGE_COUNT); if (msgList!= null) { for (Message msg : msgList) { mMsgList.add(0, msg); } if (msgList.size() > 0) { checkSendingImgMsg(); mOffset = msgList.size(); mHasLastPage = true; } else { mOffset = 0; mHasLastPage = false; } notifyDataSetChanged(); } } } public int getOffset() { return mOffset; } public boolean isHasLastPage() { return mHasLastPage; } public void refreshStartPosition() { mStart += mOffset; } //当有新消息加到MsgList，自增mStart private void incrementStartPosition() { ++mStart; } public void initMediaPlayer() { mp.reset(); } /** * 检查图片是否处于创建状态，如果是，则加入发送队列 */ private void checkSendingImgMsg() { for (Message msg : mMsgList) { if (msg.getStatus() == MessageStatus.created && msg.getContentType() == ContentType.image) { mMsgQueue.offer(msg); } } } //发送图片 将图片加入发送队列 public void setSendImg(int[] msgIds) { Message msg; if (mIsGroup) { mConv = JMessageClient.getGroupConversation(mGroupId); } else { mConv = JMessageClient.getSingleConversation(mTargetId, mTargetAppKey); Log.d(TAG, \\"mTargetAppKey: \\" + mTargetAppKey); } for (int msgId : msgIds) { msg = mConv.getMessage(msgId); if (msg!= null) { mMsgList.add(msg); incrementStartPosition(); mMsgQueue.offer(msg); } } if (mMsgQueue.size() > 0) { Message message = mMsgQueue.element(); sendNextImgMsg(message); notifyDataSetChanged(); } } /** * 从发送队列中出列，并发送图片 * * @param msg 图片消息 */ private void sendNextImgMsg(Message msg) { JMessageClient.sendMessage(msg); msg.setOnSendCompleteCallback(new BasicCallback() { @Override public void gotResult(int i, String s) { //出列 mMsgQueue.poll(); //如果队列不为空，则继续发送下一张 if (!mMsgQueue.isEmpty()) { sendNextImgMsg(mMsgQueue.element()); } notifyDataSetChanged(); } }); } @Override public long getItemId(int position) { return position; } public void releaseMediaPlayer() { if (mp!= null) mp.release(); } public void addMsgToList(Message msg) { mMsgList.add(msg); incrementStartPosition(); mActivity.runOnUiThread(new Runnable() { @Override public void run() { notifyDataSetChanged(); } }); } public Message getLastMsg() { if (mMsgList.size() > 0) { return mMsgList.get(mMsgList.size() - 1); } else { return null; } } public Message getMessage(int position) { return mMsgList.get(position); } public void removeMessage(int position) { mMsgList.remove(position); notifyDataSetChanged(); } @Override public int getCount() { return mMsgList.size(); } @Override public int getItemViewType(int position) { Message msg = mMsgList.get(position); //是文字类型或者自定义类型（用来显示群成员变化消息） switch (msg.getContentType()) { case text: return msg.getDirect() == MessageDirect.send? TYPE_SEND_TXT : TYPE_RECEIVE_TXT; case image: return msg.getDirect() == MessageDirect.send? TYPE_SEND_IMAGE : TYPE_RECEIVER_IMAGE; case voice: return msg.getDirect() == MessageDirect.send? TYPE_SEND_VOICE : TYPE_RECEIVER_VOICE; case location: return msg.getDirect() == MessageDirect.send? TYPE_SEND_LOCATION : TYPE_RECEIVER_LOCATION; case eventNotification: return TYPE_GROUP_CHANGE; default: return TYPE_CUSTOM_TXT; } } public int getViewTypeCount() { return 11; } private View createViewByType(Message msg, int position) { // 会话类型 switch (msg.getContentType()) { case image: return getItemViewType(position) == TYPE_SEND_IMAGE? mInflater .inflate(IdHelper.getLayout(mContext, \\"jmui_chat_item_send_image\\"), null) : mInflater .inflate(IdHelper.getLayout(mContext, \\"jmui_chat_item_receive_image\\"), null); case voice: return getItemViewType(position) == TYPE_SEND_VOICE? mInflater .inflate(IdHelper.getLayout(mContext, \\"jmui_chat_item_send_voice\\"), null) : mInflater .inflate(IdHelper.getLayout(mContext, \\"jmui_chat_item_receive_voice\\"), null); case location: return getItemViewType(position) == TYPE_SEND_LOCATION? mInflater .inflate(IdHelper.getLayout(mContext, \\"jmui_chat_item_send_location\\"), null) : mInflater .inflate(IdHelper.getLayout(mContext, \\"jmui_chat_item_receive_location\\"), null); case eventNotification: if (getItemViewType(position) == TYPE_GROUP_CHANGE) return mInflater.inflate(IdHelper.getLayout(mContext, \\"jmui_chat_item_group_change\\"), null); case text: return getItemViewType(position) == TYPE_SEND_TXT? mInflater .inflate(IdHelper.getLayout(mContext, \\"jmui_chat_item_send_text\\"), null) : mInflater .inflate(IdHelper.getLayout(mContext, \\"jmui_chat_item_receive_text\\"), null); default: return mInflater.inflate(IdHelper.getLayout(mContext, \\"jmui_chat_item_group_change\\"), null); } } @Override public Message getItem(int position) { return mMsgList.get(position); } public void setAudioPlayByEarPhone(int state) { AudioManager audioManager = (AudioManager) mContext .getSystemService(Context.AUDIO_SERVICE); int currVolume = audioManager.getStreamVolume(AudioManager.STREAM_VOICE_CALL); audioManager.setMode(AudioManager.MODE_IN_CALL); if (state == 0) { mIsEarPhoneOn = false; audioManager.setSpeakerphoneOn(true); audioManager.setStreamVolume(AudioManager.STREAM_VOICE_CALL, audioManager.getStreamMaxVolume(AudioManager.STREAM_VOICE_CALL), AudioManager.STREAM_VOICE_CALL); Log.i(TAG, \\"set SpeakerphoneOn true!\\"); } else { mIsEarPhoneOn = true; audioManager.setSpeakerphoneOn(false); audioManager.setStreamVolume(AudioManager.STREAM_VOICE_CALL, currVolume, AudioManager.STREAM_VOICE_CALL); Log.i(TAG, \\"set SpeakerphoneOn false!\\"); } } public void clearMsgList() { mMsgList.clear(); mStart = 0; notifyDataSetChanged(); } @Override public View getView(final int position, View convertView, ViewGroup parent) { final Message msg = mMsgList.get(position); final UserInfo userInfo = msg.getFromUser(); final ViewHolder holder; if (convertView == null) { holder = new ViewHolder(); convertView = createViewByType(msg, position); switch (msg.getContentType()) { case text: holder.headIcon = (CircleImageView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_avatar_iv\\")); holder.displayName = (TextView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_display_name_tv\\")); holder.txtContent = (TextView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_msg_content\\")); holder.sendingIv = (ImageView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_sending_iv\\")); holder.resend = (ImageButton) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_fail_resend_ib\\")); break; case image: holder.headIcon = (CircleImageView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_avatar_iv\\")); holder.displayName = (TextView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_display_name_tv\\")); holder.picture = (ImageView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_picture_iv\\")); holder.sendingIv = (ImageView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_sending_iv\\")); holder.progressTv = (TextView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_progress_tv\\")); holder.resend = (ImageButton) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_fail_resend_ib\\")); break; case voice: holder.headIcon = (CircleImageView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_avatar_iv\\")); holder.displayName = (TextView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_display_name_tv\\")); holder.txtContent = (TextView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_msg_content\\")); holder.voice = (ImageView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_voice_iv\\")); holder.sendingIv = (ImageView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_sending_iv\\")); holder.voiceLength = (TextView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_voice_length_tv\\")); holder.readStatus = (ImageView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_read_status_iv\\")); holder.resend = (ImageButton) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_fail_resend_ib\\")); break; case location: holder.headIcon = (CircleImageView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_avatar_iv\\")); holder.displayName = (TextView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_display_name_tv\\")); holder.txtContent = (TextView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_msg_content\\")); holder.sendingIv = (ImageView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_sending_iv\\")); holder.resend = (ImageButton) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_fail_resend_ib\\")); break; case eventNotification: holder.groupChange = (TextView) convertView .findViewById(IdHelper.getViewID(mContext, \\"jmui_group_content\\")); break; default: holder.groupChange = (TextView) convertView .findViewById(IdHelper.getViewID