gs(first=yes,second=yes); // Set tag and group on the Wizard processWizardPage.setTags(process.getTags()); // Assert that there are two Tags on the Wizard window processWizardPage.isTagsDisplayed(0, true); processWizardPage.isTagsDisplayed(1, true); // Delete the tags processWizardPage.deleteTags(); // Assert that there is only one Tag on the Wizard window processWizardPage.isTagsDisplayed(0, true); processWizardPage.isTagsDisplayed(1, false); // Assert that the Tag value is empty on the Wizard window Assert.assertEquals(processWizardPage.getTagKeyText(0), , Tag Key Should be empty on the Wizard window); Assert.assertEquals(processWizardPage.getTagValueText(0), , Tag Value Should be empty on the Wizard window); } /* Step 2 tests */ @Test public void testTimingStepDefaultScenario() throws Exception{ // Set values on the General Info Page processWizardPage.setProcessGeneralInfo(process); processWizardPage.clickNext(); // Set values on the Properties Page processWizardPage.setProcessPropertiesInfo(process); processWizardPage.clickNext(); // Assert that user is able to go to next page processWizardPage.isValidityStartDateDisplayed(true); } @Test public void testTimingStepXmlPreview() throws Exception{ // Set values on the General Info Page processWizardPage.setProcessGeneralInfo(process); processWizardPage.clickNext(); // Set values on the Properties Page processWizardPage.setProcessPropertiesInfo(process); ProcessMerlin processFromXML = processWizardPage.getEntityFromXMLPreview(); // Assert all the values entered on the Properties Page LOGGER.info(String.format(Comparing source process: %n%s%n and preview: %n%s%n., process, processFromXML)); process.assertPropertiesInfo(processFromXML); } @Test public void testTimingStepEditXml() throws Exception{ // Set values on the General Info Page processWizardPage.setProcessGeneralInfo(process); processWizardPage.clickNext(); processWizardPage.setFrequencyQuantity(10); processWizardPage.setFrequencyUnit(minutes); processWizardPage.setMaxParallelInstances(5); ProcessMerlin processFromXML = processWizardPage.getEntityFromXMLPreview(); processFromXML.setFrequency(null); processFromXML.setParallel(1); String xmlToString = processFromXML.toString(); processWizardPage.setXmlPreview(xmlToString); Assert.assertEquals(processWizardPage.getFrequencyQuantityText(), , Frequency Quantity Should be empty on the Wizard window); Assert.assertEquals(processWizardPage.getMaxParallelInstancesText(), 1, Unexpected Parallel on the Wizard window); processFromXML = processWizardPage.getEntityFromXMLPreview(); // Set TimeZone and Order TimeZone tz = TimeZone.getTimeZone(GMT-08:00); processFromXML.setTimezone(tz); processFromXML.setOrder(ExecutionType.LIFO); xmlToString = processFromXML.toString(); processWizardPage.setXmlPreview(xmlToString); // Assert TimeZone and Order Assert.assertEquals(processWizardPage.getOrderText(), LIFO, Unexpected Order on the Wizard window); Assert.assertEquals(processWizardPage.getTimezoneText(), GMT-08:00, Unexpected TimeZone on the Wizard window); } @Test public void testTimingStepDropDownLists() throws Exception{ // Set values on the General Info Page processWizardPage.setProcessGeneralInfo(process); processWizardPage.clickNext(); // Assert dropdown values List<String> dropdownValues = processWizardPage.getTimezoneValues(); Assert.assertEquals(timeZones, dropdownValues, TimeZone Values Are Not Equal); dropdownValues = processWizardPage.getFrequencyUnitValues(); Assert.assertEquals(timeUnits, dropdownValues, Frequency Unit Values Are Not Equal); dropdownValues = processWizardPage.getMaxParallelInstancesValues(); Assert.assertEquals(parallel, dropdownValues, Max Parallel Values Are Not Equal); dropdownValues = processWizardPage.getOrderValues(); Assert.assertEquals(order, dropdownValues, Order Unit Values Are Not Equal); dropdownValues = processWizardPage.getRetryPolicyValues(); Assert.assertEquals(policy, dropdownValues, Retry Policy Values Are Not Equal); dropdownValues = processWizardPage.getRetryDelayUnitValues(); Assert.assertEquals(delayTimeUnits, dropdownValues, Retry Delay Unit Values Are Not Equal); } /* Step 3 tests */ @Test public void testClustersStepDefaultScenario() throws URISyntaxException, IOException, AuthenticationException, InterruptedException, JAXBException { bundles[0].submitClusters(cluster); processWizardPage.setProcessGeneralInfo(process); processWizardPage.clickNext(); processWizardPage.setProcessPropertiesInfo(process); processWizardPage.clickNext(); processWizardPage.setProcessClustersInfo(process); processWizardPage.clickNext(); processWizardPage.isAddInputButtonDisplayed(true); } @Test public void testClustersStepDropDownList() throws URISyntaxException, IOException, AuthenticationException, InterruptedException, JAXBException { List<String> clusters = new ArrayList<>(); ClusterMerlin clusterMerlin = bundles[0].getClusterElement(); String clusterName = clusterMerlin.getName(); for(int i = 1; i < 6; i++) { clusterMerlin.setName(clusterName + i); AssertUtil.assertSucceeded(cluster.getClusterHelper().submitEntity(clusterMerlin.toString())); clusters.add(clusterMerlin.getName()); } processWizardPage.setProcessGeneralInfo(process); processWizardPage.clickNext(); processWizardPage.setProcessPropertiesInfo(process); processWizardPage.clickNext(); List<String> dropdownValues = new ArrayList<>(processWizardPage.getClustersFromDropDown()); String entitiesPrefix = Util.getEntityPrefix(this); for(int i = 0; i < dropdownValues.size(); i++) { if (!dropdownValues.get(i).contains(entitiesPrefix)) { dropdownValues.remove(i); } } Collections.sort(clusters); Collections.sort(dropdownValues); Assert.assertEquals(clusters, dropdownValues, Clusters Drop Down Values Are Not Equal); } @Test public void testClustersStepPopupCalendars() { // Set values on the General Info Page processWizardPage.setProcessGeneralInfo(process); processWizardPage.clickNext(); // Set values on the Properties Page processWizardPage.setProcessPropertiesInfo(process); processWizardPage.clickNext(); processWizardPage.clickOnValidityStart(); processWizardPage.clickOnValidityEnd(); } @Test public void testClustersStepAddDeleteCluster() throws Exception { bundles[0].submitClusters(cluster); ClusterMerlin clusterMerlin = bundles[0].getClusterElement(); clusterMerlin.setName(clusterMerlin.getName() + 1); AssertUtil.assertSucceeded(cluster.getClusterHelper().submitEntity(clusterMerlin.toString())); Cluster processCluster = new Cluster(); processCluster.setName(clusterMerlin.getName()); processCluster.setValidity(process.getClusters().getClusters().get(0).getValidity()); process.addProcessCluster(processCluster); // Set values on the General Info Page processWizardPage.setProcessGeneralInfo(process); processWizardPage.clickNext(); // Set values on the Properties Page processWizardPage.setProcessPropertiesInfo(process); processWizardPage.clickNext(); processWizardPage.setClusters(process.getClusters()); ProcessMerlin xmlPreview = processWizardPage.getEntityFromXMLPreview(); LOGGER.info(String.format(Comparing clusters of process: %n%s%n and preview: %n%s%n., process, xmlPreview)); ProcessMerlin.assertClustersEqual(process.getClusters().getClusters(), xmlPreview.getClusters().getClusters()); processWizardPage.deleteLastCluster(); xmlPreview = processWizardPage.getEntityFromXMLPreview(); process.getClusters().getClusters().remove(1); LOGGER.info(String.format(Comparing clusters of process: %n%s%n and preview: %n%s%n., process, xmlPreview)); ProcessMerlin.assertClustersEqual(process.getClusters().getClusters(), xmlPreview.getClusters().getClusters()); } @Test public void testClusterStepEditXml() throws Exception { bundles[0].submitClusters(cluster); ClusterMerlin clusterMerlin = bundles[0].getClusterElement(); String firstClusterName = clusterMerlin.getName(); clusterMerlin.setName(firstClusterName + 2); AssertUtil.assertSucceeded(cluster.getClusterHelper().submitEntity(clusterMerlin.toString())); //set values on the General Info Page p